import { Component, OnInit, AfterViewInit, ViewChild } from '@angular/core';
import { JuegosService } from './core/services/juegos.service';
import { Juego } from './shared/models/juego.model';
import { RegistroComponent } from './features/registro/registro.component';

declare var bootstrap: any;

@Component({
  standalone: false,
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit, AfterViewInit {
  title = 'duoc-games';
  juegos: Juego[] = [];
  juegosFiltrados: Juego[] = [];
  categoriaSeleccionada: string | null = null;
  cargando = true;

  @ViewChild(RegistroComponent) registroComponent!: RegistroComponent;

  // Mapa de asociación entre categorías locales y géneros de la API
  categoriaGeneroMap: { [key: string]: string[] } = {
    'Estrategia': ['Strategy', 'fantasy', 'mmorpg', 'battle-royale'],
    'Familiares': ['racing'],
    'Fiesta': ['sports'],
    'Infantiles': ['anime', 'social', 'sports']
  };

  constructor(private juegosService: JuegosService) {}

  ngOnInit() {
    this.juegosService.obtenerJuegos().subscribe(data => {
      this.juegos = data;
      this.filtrarJuegos();
      this.cargando = false;
    });
  }

  ngAfterViewInit() {
    const modal = document.getElementById('modalRegistro');
    if (modal) {
      modal.addEventListener('hidden.bs.modal', () => {
        if (this.registroComponent) {
          this.registroComponent.limpiarFormulario();
        }
      });
    }
  }

  onCategoriaSeleccionada(categoria: string | null) {
    this.categoriaSeleccionada = categoria;
    this.filtrarJuegos();
  }

  filtrarJuegos() {
    if (!this.categoriaSeleccionada) {
      this.juegosFiltrados = this.juegos;
    } else {
      const generos = this.categoriaGeneroMap[this.categoriaSeleccionada] || [];
      this.juegosFiltrados = this.juegos.filter(j => generos.some(g => j.genre && j.genre.toLowerCase().includes(g.toLowerCase())));
    }
  }
}

<header>
  <nav class="navbar navbar-expand-lg navbar-dark" style="background-color: #2a9d8f;">
    <div class="container-fluid">
      <a class="navbar-brand fw-bold" href="#">Duoc-Games</a>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
        <ul class="navbar-nav ms-auto">
          <li class="nav-item">
            <button type="button" class="btn btn-ludoteca ms-3" data-bs-toggle="modal" data-bs-target="#modalRegistro">
              Registrarse
            </button>
          </li>
        </ul>
      </div>
    </div>
  </nav>
</header>
<main class="container py-4">
  <div class="row mb-3">
    <div class="col-12">
      <app-filtros (categoriaSeleccionada)="onCategoriaSeleccionada($event)"></app-filtros>
    </div>
  </div>
  <app-categoria-imagen [nombreCategoria]="categoriaSeleccionada"></app-categoria-imagen>
  <app-juegos-list [juegos]="juegosFiltrados" [juegosPorCarga]="3"></app-juegos-list>
</main>
<footer>
  <p>&copy; 2025 Duoc-Games</p>
</footer>
<!-- Modal de Registro (puedes integrar <app-registro> aquí si lo deseas) -->
