import { Component, Input, OnChanges } from '@angular/core';
import { Juego } from '../../../shared/models/juego.model';

@Component({
  standalone: false,
  selector: 'app-juegos-list',
  templateUrl: './juegos-list.component.html',
  styleUrls: ['./juegos-list.component.css']
})
export class JuegosListComponent implements OnChanges {
  @Input() juegos: Juego[] = [];
  @Input() juegosPorCarga = 3;

  juegosMostrados: Juego[] = [];
  private cantidadMostrada = 0;

  ngOnChanges() {
    this.cantidadMostrada = 0;
    this.cargarMas();
  }

  cargarMas() {
    const siguiente = this.cantidadMostrada + this.juegosPorCarga;
    this.juegosMostrados = this.juegos.slice(0, siguiente);
    this.cantidadMostrada = this.juegosMostrados.length;
  }
}
<div class="row">
  <div *ngFor="let juego of juegosMostrados" class="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
    <div class="card h-100 shadow-sm">
      <img [src]="juego.thumbnail" class="card-img-top" [alt]="juego.title" style="height: 180px; object-fit: cover;">
      <div class="card-body d-flex flex-column">
        <h5 class="card-title">{{ juego.title }}</h5>
        <p class="card-text small flex-grow-1">{{ juego.short_description }}</p>
        <p class="card-text"><span class="badge badge-genero">{{ juego.genre }}</span></p>
      </div>
    </div>
  </div>
</div>
<div class="loadmore-container" *ngIf="juegosMostrados.length < juegos.length">
  <button class="btn btn-ludoteca" (click)="cargarMas()">Cargar más</button>
</div>
<div *ngIf="juegos.length === 0">
  <p class="text-center">No hay juegos para mostrar en esta categoría.</p>
</div>
